// client/src/pages/Dashboard.js

import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import * as jwt_decode from 'jwt-decode';

const Dashboard = () => {
  const [username, setUsername] = useState('');
  const [isAdmin, setIsAdmin] = useState(false); // Tambahkan state untuk menandai apakah pengguna adalah admin
  const history = useHistory();

  useEffect(() => {
    const accessToken = localStorage.getItem('accessToken');

    if (accessToken) {
      const decodedToken = jwt_decode(accessToken);

      setUsername(decodedToken.username);
      setIsAdmin(decodedToken.isAdmin); // Tentukan apakah pengguna adalah admin berdasarkan token
    } else {
      history.push('/login');
    }
  }, [history]);

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    history.push('/login');
  };

  return (
    <div>
      <h2>Welcome, {username}!</h2>
      {isAdmin ? (
        <div>
          <h3>Admin Dashboard</h3>
          {/* Tampilkan konten admin */}
        </div>
      ) : (
        <div>
          <h3>User Dashboard</h3>
          {/* Tampilkan konten pengguna */}
        </div>
      )}
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Dashboard;
