// PromotionSlider.js
import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import imgPromotion1 from '../assets/promo.png';
import imgPromotion2 from '../assets/promo2.png';
import imgPromotion3 from '../assets/promo3.png';

const PromotionSlider = () => {
  const sliderStyle = {
    margin: '20px 0', // Tambahkan margin atas dan bawah
    padding: '10px', // Tambahkan padding
    border: '1px solid #ddd', // Tambahkan border
    borderRadius :'20px', 
    width: '20px',
  };

  return (
    <div style={sliderStyle}>
      <Carousel showArrows={true} infiniteLoop={true} showThumbs={false}>
        <div>
          <img src={imgPromotion1} alt="Promotion 1" style={{ width: '50%', height: 'auto', marginRight: '10px' }} />
        </div>
        <div>
          <img src={imgPromotion2} alt="Promotion 2" style={{ width: '50%', height: 'auto', marginRight: '10px' }} />
        </div>
        <div>
          <img src={imgPromotion3} alt="Promotion 3" style={{ width: '50%', height: 'auto', marginRight: '10px' }} />
        </div>
      </Carousel>
    </div>
  );
};

export default PromotionSlider;
