import React from 'react';

const ItemVariants = ({ variants, onSelectVariant, selectedPayment }) => {
  const handlePaymentChange = (payment) => {
    // Anda dapat menambahkan logika atau panggilan fungsi onSelectPayment di sini
    // untuk menangani perubahan opsi pembayaran
  };

  return (
    <div>
      {variants.map((variant) => (
        <div key={variant.id}>
          <label>
            <input
              type="checkbox"
              checked={variant.selected}
              onChange={() => onSelectVariant(variant)}
            />
            <div>
              <h3>{variant.nominal}</h3>
              <p>{variant.price}</p>
            </div>
          </label>
        </div>
      ))}
      <div>
        <h3>Pilih Pembayaran</h3>
        <label>
          <input
            type="radio"
            name="payment"
            value="dana"
            checked={selectedPayment === 'dana'}
            onChange={() => handlePaymentChange('dana')}
          />
          Dana
        </label>
        <label>
          <input
            type="radio"
            name="payment"
            value="gopay"
            checked={selectedPayment === 'gopay'}
            onChange={() => handlePaymentChange('gopay')}
          />
          GoPay
        </label>
        <label>
          <input
            type="radio"
            name="payment"
            value="ovo"
            checked={selectedPayment === 'ovo'}
            onChange={() => handlePaymentChange('ovo')}
          />
          OVO
        </label>
      </div>
    </div>
  );
};

export default ItemVariants;
