import React, { useState } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import imgPromotion1 from '../assets/promo.png';
import imgPromotion2 from '../assets/promo2.png';
import imgPromotion3 from '../assets/promo3.png';

const PromotionSlider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleSlideChange = (index) => {
    setCurrentSlide(index);
  };

  const sliderContainerStyle = {
    position: 'relative',
    margin: '20px 0',
  };

  const arrowStyle = {
    position: 'absolute',
    top: '50%',
    transform: 'translateY(-50%)',
    backgroundColor: 'rgba(255, 255, 255, 0.5)',
    padding: '10px',
    borderRadius: '50%', // Mengubah ke bentuk bulat
    cursor: 'pointer',
  };

  const arrowLeftStyle = {
    ...arrowStyle,
    left: '10px',
  };

  const arrowRightStyle = {
    ...arrowStyle,
    right: '10px',
  };

  return (
    <div style={sliderContainerStyle}>
      <Carousel showArrows={false} infiniteLoop={true} showThumbs={false} onChange={handleSlideChange}>
        <div>
          <img src={imgPromotion1} alt="Promotion 1" style={{ width: '50%', height: 'auto', marginRight: '10px' }} />
        </div>
        <div>
          <img src={imgPromotion2} alt="Promotion 2" style={{ width: '50%', height: 'auto', marginRight: '10px' }} />
        </div>
        <div>
          <img src={imgPromotion3} alt="Promotion 3" style={{ width: '50%', height: 'auto', marginRight: '10px' }} />
        </div>
      </Carousel>

      {/* Tombol Sebelumnya */}
      <div style={{ ...arrowLeftStyle, left: 'calc(50% - 250px)' }} onClick={() => setCurrentSlide(currentSlide - 1)}>
        {'<'}
      </div>

      {/* Tombol Selanjutnya */}
      <div style={{ ...arrowRightStyle, right: 'calc(50% - 250px)' }} onClick={() => setCurrentSlide(currentSlide + 1)}>
        {'>'}
      </div>
    </div>
  );
};

export default PromotionSlider;
