// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect, useHistory } from 'react-router-dom'; // Import useHistory
import Home from './components/Home';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import Register from './components/Register';

const App = () => {
  const [user, setUser] = useState(null);
  const history = useHistory(); // Access the history object

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    setUser(null);
    // Redirect to the login page (or any other desired destination)
    history.push('/login');
  };

  const handleRegister = (userData) => {
    setUser(userData);
  };

  return (
    <Router>
      <Switch>
        <Route path="/home">
          {user ? <Home user={user} /> : <Redirect to="/login" />}
        </Route>
        <Route
          path="/login"
          render={(props) => (
            <Login {...props} onLogin={handleLogin} />
          )}
        />
        <Route
          path="/register"
          render={(props) => (
            <Register {...props} onRegister={handleRegister} />
          )}
        />
        <Route
          path="/dashboard"
          render={(props) =>
            user ? (
              <Dashboard {...props} onLogout={handleLogout} />
            ) : (
              <Redirect to="/login" />
            )
          }
        />
      </Switch>
    </Router>
  );
};

export default App;


