// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';

const Dashboard = ({ user }) => {
  const history = useHistory();

  useEffect(() => {
    // Jika pengguna tidak memiliki peran (role) "admin," redirect ke halaman login
    if (!user || user.role !== 'admin') {
      history.push('/login');
    }
  }, [user, history]);

  const handleLogout = () => {
    // Hapus informasi peran (role) dari state
    setUser(null);

    // Hapus token akses dari penyimpanan lokal
    localStorage.removeItem('accessToken');

    // Redirect ke halaman login
    history.push('/login');
  };

  return (
    <div>
      <h2>Welcome, {user.username}!</h2>
      {user.role === 'admin' && <p>You have admin privileges.</p>}
      <button onClick={handleLogout}>Logout</button>
      {/* Isi dashboard lainnya */}
    </div>
  );
};

export default Dashboard;
