import React, { useState } from 'react';

const Valorant = () => {
  const [riotId, setRiotId] = useState('');

  const items = [
    { points: 125, price: 15000 },
    { points: 420, price: 80000 },
    // tambahkan item lainnya di sini
  ];

  const handleItemClick = (item) => {
    // lakukan sesuatu dengan item yang diklik
    console.log(item);
  };

  return (
    <div>
      <h1>Selamat datang white#457</h1>
      <input
        type="text"
        placeholder="Masukkan Riot ID Anda"
        value={riotId}
        onChange={(e) => setRiotId(e.target.value)}
      />
      <h2>Pilih nominal satu jam terakhir</h2>
      <div>
        {items.map((item, index) => (
          <div key={index} onClick={() => handleItemClick(item)}>
            <h3>{item.points} Points</h3>
            <p>Rp. {item.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Valorant;
