// Header.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import logo from '../assets/logo.png';

const Header = ({ setLoggedInStatus }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check for an existing authentication token
    const accessToken = localStorage.getItem('accessToken');
    setIsLoggedIn(!!accessToken); // Set isLoggedIn menjadi true jika accessToken ada
  }, [setLoggedInStatus]); // Menambahkan setLoggedInStatus sebagai dependency agar useEffect dijalankan setiap kali setLoggedInStatus berubah

  return (
    <header className='header-container'>
      <header className='header-navbar'>
        {/* ... kode lainnya ... */}

        {/* Tombol Login dan Logout di luar menu */}
        {!isLoggedIn ? (
          <Link to="/login" className="login-button" style={{ textDecoration: 'none', color: 'white' }}>
            <div>
              <span>Login</span>
            </div>
          </Link>
        ) : (
          // Tambahkan button logout ketika sudah login
          <Link to="/" className="logout-button" onClick={() => { setIsLoggedIn(false); setLoggedInStatus(false); localStorage.removeItem('accessToken'); }} style={{ textDecoration: 'none', color: 'white' }}>
            <div>
              <span>Logout</span>
            </div>
          </Link>
        )}

        {/* Tombol Daftar di luar menu */}
        {!isLoggedIn && (
          <Link to="/register" className="register-button" style={{ textDecoration: 'none', color: 'white' }}>
            <div>
              <span>Daftar</span>
            </div>
          </Link>
        )}
      </header>
    </header>
  );
};

export default Header;
