// src/pages/Home.js
import React from 'react';
import { Link } from 'react-router-dom';

import imgValorant from '../assets/valorant.jpg';

const Home = () => {
  return (
    <div>
      <h1>Selamat Datang di Top-Up Valorant Shop</h1>
      <p>Temukan penawaran terbaik untuk top-up Valorant di sini!</p>

      {/* Informasi Top-Up */}
      <div>
        <h2>Top-Up Valorant</h2>
        <p>Top-up saldo Valorant Anda dan dapatkan bonus eksklusif!</p>
        <p>Harga mulai dari $10.00</p>
        <Link to="/topup">
          <button>Beli Sekarang</button>
        </Link>
      </div>

      {/* Promo atau Banner */}
      <div>
        <Link to="/games/valorant">
          <img src={imgValorant} alt="Promo Banner" style={{width: '60px',height:'auto'}}/> 
        </Link>
      </div>

      {/* Testimoni Pelanggan */}
      <div>
        <h2>Testimoni Pelanggan</h2>
        <p>"Top-up Valorant di sini sangat mudah dan cepat!" - John Doe</p>
      </div>

      {/* Kontak Kami */}
      <div>
        <h2>Hubungi Kami</h2>
        <p>Jika Anda memiliki pertanyaan, silakan hubungi kami di support@example.com.</p>
      </div>
    </div>
  );
};

export default Home;
