import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { handleAddItem } from '../components/admins/addItem';
import * as jwt_decode from 'jwt-decode';

const Dashboard = () => {
  const [username, setUsername] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const history = useHistory();

  useEffect(() => {
    const accessToken = localStorage.getItem('accessToken');

    if (accessToken) {
      const decodedToken = jwt_decode(accessToken);

      setUsername(decodedToken.username);
      setIsAdmin(decodedToken.role === 'admin'); // Periksa apakah peran pengguna adalah 'admin'
    } else {
      history.push('/login');
    }
  }, [history]);

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    history.push('/login');
  };

  return (
    <div>
      <h2>Welcome, {username}!</h2>
      {isAdmin ? (
        <div>
          <h3>Admin Dashboard</h3>
          {/* Tampilkan konten admin */}
          <button onClick={handleAddItem}>Tambah Item</button>
        </div>
      ) : (
        <div>
          <h3>User Dashboard</h3>
          {/* Tampilkan konten pengguna */}
          <p>Ini adalah konten khusus pengguna.</p>
        </div>
      )}
      <button onClick={handleLogout}>Logout</button>
    </div>
  );


// Di dalam komponen Anda...
const addItem = () => {
