import React, { useEffect, useState } from 'react';
import { jwtDecode } from 'jwt-decode';
import { useHistory } from 'react-router-dom';
import jwt_decode from 'jwt-decode';

const Dashboard = () => {
    const [username, setUsername] = useState('');
    const history = useHistory();
  
    useEffect(() => {
      const accessToken = localStorage.getItem('accessToken');
  
      // Periksa apakah token akses tersedia dan masih valid
      if (accessToken) {
        const decodedToken = jwt_decode(accessToken);
  
        // Periksa waktu kedaluwarsa token di sini jika diperlukan
        // Jika token masih valid, set username dan biarkan pengguna tetap login
        setUsername(decodedToken.username);
      } else {
        // Jika tidak ada token akses, redirect ke halaman login
        history.push('/login');
      }
    }, [history]);
  
    const handleLogout = () => {
      // Hapus token akses dari penyimpanan lokal
      localStorage.removeItem('accessToken');
  
      // Redirect ke halaman login
      history.push('/login');
    };
  
    return (
      <div>
        <h2>Welcome, {username}!</h2>
        <button onClick={handleLogout}>Logout</button>
        {/* Isi dashboard lainnya */}
      </div>
    );
  };
  
  export default Dashboard;
