import React, { useState } from 'react';
import './Valorant.css';

const Valorant = () => {
  const [riotId, setRiotId] = useState('');
  const [points, setPoints] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Data yang dikumpulkan:', { riotId, points });
  };

  return (
    <div className="valorant-form">
      <h2>Masukkan Riot ID Anda</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-field">
          <label htmlFor="riotId">Riot ID:</label><br/>
          <input
            type="text"
            id="riotId"
            value={riotId}
            onChange={(e) => setRiotId(e.target.value)}
            required
          />
        </div>
        <div className='input-field'>
          <label htmlFor="points">Pilih nominal top up:</label><br/>
          <select
            id="points"
            value={points}
            onChange={(e) => setPoints(e.target.value)}
            required
          >
            <option value="125">125 Points</option>
            <option value="420">420 Points</option>
            <option value="700">700 Points</option>
            <option value="1375">1375 Points</option>
            <option value="2400">2400 Points</option>
          </select>
        </div>
        <button type="submit">Beli Sekarang</button>
      </form>
    </div>
  );
};

export default Valorant;
