import React, { useState } from 'react';

const Valorant = () => {
  const [riotId, setRiotId] = useState('');
  const [nominal, setNominal] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Lakukan sesuatu dengan data yang dikumpulkan, misalnya mengirim ke server
    console.log('Data yang dikumpulkan:', { riotId, nominal, paymentMethod });
  };

  return (
    <div 
    style={{ background: '#f2f2f2', padding: '20px',display: 'flex', flexDirection: 'column', alignItems: 'center', height: '100vh' }}>
      <h2>Top-Up Valorant</h2>
      <form onSubmit={handleSubmit} style={{ background: 'white', padding: '20px', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
        <div style={{ background: 'gray',borderRadius:'10px',width:'50px'}}>
          <label htmlFor="riotId">Masukkan Riot ID Anda:</label>
          <input
            type="text"
            id="riotId"
            value={riotId}
            onChange={(e) => setRiotId(e.target.value)}
            required
          />
        </div>
        <div style={{ background: 'gray'}}>
          <label htmlFor="nominal">Pilih Nominal Top-Up:</label>
          <select
            id="nominal"
            value={nominal}
            onChange={(e) => setNominal(e.target.value)}
            required
          >
            <option value="50">50 Valorant Points</option>
            <option value="100">100 Valorant Points</option>
            <option value="200">200 Valorant Points</option>
          </select>
        </div>
        <div>
          <label htmlFor="paymentMethod">Pilih Metode Pembayaran:</label>
          <select
            id="paymentMethod"
            value={paymentMethod}
            onChange={(e) => setPaymentMethod(e.target.value)}
            required
          >
            <option value="creditCard">Kartu Kredit</option>
            <option value="bankTransfer">Transfer Bank</option>
            <option value="wallet">Dompet Elektronik</option>
          </select>
        </div>
        <button type="submit">Proses Top-Up</button>
      </form>
    </div>
  );
};

export default Valorant;
