import React, { useState, useEffect, lazy, Suspense } from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import Header from './components/Header';

const Home = lazy(() => import('./pages/Home'));
const Login = lazy(() => import('./pages/Login'));
const Dashboard = lazy(() => import('./pages/Dashboard'));
const Register = lazy(() => import('./pages/Register'));
const AdminRegistration = lazy(() => import('./components/AdminRegistration'));
const TopUp = lazy(() => import('./pages/TopUp'));
const Valorant = lazy(() => import('./pages/Valorant'));


const App = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Check for an existing authentication token
    const accessToken = localStorage.getItem('accessToken');

    if (accessToken) {
      // Token found, set initial user state
      setUser({ accessToken });
    }
  }, []); // Empty dependency array ensures the effect runs once on mount

  const handleLogin = (userData) => {
    setUser(userData);
    // Save the access token to local storage
    localStorage.setItem('accessToken', userData.accessToken);
  };

  const handleLogout = () => {
    setUser(null);
    // Remove the access token from local storage
    localStorage.removeItem('accessToken');
  };

  const handleRegister = (userData) => {
    setUser(userData);
    // Save the access token to local storage
    localStorage.setItem('accessToken', userData.accessToken);
  };

  return (
    <Router>
      <Header />
      <Suspense fallback={<div>Loading...</div>}>
        <Switch>
          <Route path="/home" component={Home} />
          <Route path="/login" render={(props) => <Login {...props} onLogin={handleLogin} />} />
          <Route path="/register" render={(props) => <Register {...props} onRegister={handleRegister} />} />
          <Route path="/admin/register" component={AdminRegistration} />
          <Route path="/dashboard" render={(props) => (user ? <Dashboard {...props} onLogout={handleLogout} /> : <Redirect to="/login" />)} />
          <Route path="/topup" component={TopUp} />
          <Route path="/games/valorant" component={Valorant} />
          <Redirect from="/" to="/home" />
        </Switch>
      </Suspense>
    </Router>
  );
};

export default App;
