import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import axios from 'axios';

import Home from './components/Home';
import Login from './components/Login';
import Dashboard from './components/Dashboard';

const App = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const accessToken = localStorage.getItem('accessToken');

    if (accessToken) {
      // Verifikasi token akses pada sisi frontend
      axios.post('http://localhost:5001/auth/verify-token', { accessToken })
        .then(response => {
          setUser(response.data.user);
        })
        .catch(error => {
          console.error('Token verification failed:', error.response.data.message);
          // Optional: Handle error, misalnya, hapus token yang kadaluarsa
          localStorage.removeItem('accessToken');
        });
    }
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('accessToken');
  };

  return (
    <Router>
      <Switch>
        <Route path="/home" component={Home} />
        <Route
          path="/login"
          render={() => (user ? <Redirect to="/dashboard" /> : <Login onLogin={handleLogin} />)}
        />
        <Route
          path="/dashboard"
          render={() => (user ? <Dashboard user={user} onLogout={handleLogout} /> : <Redirect to="/login" />)}
        />
      </Switch>
    </Router>
  );
};

export default App;


