// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import Home from './components/Home';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import Register from './components/Register';

const App = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Check for an existing authentication token
    const accessToken = localStorage.getItem('accessToken');

    if (accessToken) {
      // Token found, set initial user state
      setUser({ accessToken });
    }
  }, []); // Empty dependency array ensures the effect runs once on mount

  const handleLogin = (userData) => {
    setUser(userData);
    // Save the access token to local storage
    localStorage.setItem('accessToken', userData.accessToken);
  };

  const handleLogout = () => {
    setUser(null);
    // Remove the access token from local storage
    localStorage.removeItem('accessToken');
  };

  const handleRegister = (userData) => {
    setUser(userData);
    // Save the access token to local storage
    localStorage.setItem('accessToken', userData.accessToken);
  };

  return (
    <Router>
      <Switch>
        <Route path="/home">
          {user ? <Home user={user} /> : <Redirect to="/login" />}
        </Route>
        <Route
          path="/login"
          render={(props) => (
            <Login {...props} onLogin={handleLogin} />
          )}
        />
        <Route
          path="/register"
          render={(props) => (
            <Register {...props} onRegister={handleRegister} />
          )}
        />
        <Route
          path="/dashboard"
          render={(props) =>
            user ? (
              <Dashboard {...props} onLogout={handleLogout} />
            ) : (
              <Redirect to="/login" />
            )
          }
        />
      </Switch>
    </Router>
  );
};

export default App;


