import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import * as jwt_decode from 'jwt-decode';

const Dashboard = () => {
  const [items, setItems] = useState([]);
  const [username, setUsername] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [gameName, setGameName] = useState(''); // Define gameName state
  const [itemName, setItemName] = useState(''); // Define itemName state
  const [itemDetails, setItemDetails] = useState(''); // Define itemDetails state
  const [selectedImage, setSelectedImage] = useState(null); // Define selectedImage state
  const history = useHistory();


  useEffect(() => {
    const accessToken = localStorage.getItem('accessToken');
  
    if (accessToken) {
      const decodedToken = jwt_decode(accessToken);
  
      setUsername(decodedToken.username);
      setIsAdmin(decodedToken.role === 'admin'); // Periksa apakah peran pengguna adalah 'admin'
  
      // Jika pengguna adalah admin, ambil semua item
      if (decodedToken.role === 'admin') {
        fetch('http://localhost:5001/api/getItems')
          .then(response => response.json())
          .then(data => setItems(data))
          .catch(error => console.error('Error:', error));
      }
    } else {
      history.push('/login');
    }
  }, [history]);

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    history.push('/login');
  };

  const addItem = (event) => {
    event.preventDefault();
    const gameName = event.target.elements.gameName.value;
    const itemName = event.target.elements.itemName.value;
    const itemDetails = event.target.elements.itemDetails.value;

    // Menggunakan FormData untuk mendukung pengiriman file
    const formData = new FormData();
    formData.append('gameName', gameName);
    formData.append('itemName', itemName);
    formData.append('itemDetails', itemDetails);
    formData.append('image', selectedImage);

    fetch('http://localhost:5001/api/addItem', {
      method: 'POST',
      body: formData,
    })
      .then(response => response.json())
      .then(data => {
        console.log(data);
        // Anda mungkin ingin memperbarui state items di sini
        // dan mereset form setelah berhasil menambahkan item
        setItems([...items, { game_name: gameName, item_name: itemName, item_details: itemDetails }]);
        setGameName('');
        setItemName('');
        setItemDetails('');
        setSelectedImage(null);
      })
      .catch(error => {
        console.error('Error:', error);
      });
  };


  const addItemVariant = (event) => {
    event.preventDefault();
    const itemId = event.target.elements.itemId.value;
    const nominal = event.target.elements.nominal.value;
    const price = event.target.elements.price.value;

    fetch('http://localhost:5001/api/addItemVariant', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        itemId,
        nominal,
        price,
      }),
    })
    .then(response => response.json())
    .then(data => {
      console.log(data);
      // Anda mungkin ingin memperbarui state items di sini
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  };

  return (
    <div>
      <h2>Welcome, {username}!</h2>
      {isAdmin ? (
        <div>
          <h3>Admin Dashboard</h3>
          {/* Tampilkan form untuk menambahkan item */}
          <form onSubmit={addItem}>
            <input type="text" value={gameName} onChange={e => setGameName(e.target.value)} placeholder="Nama Game" required />
            <input type="text" value={itemName} onChange={e => setItemName(e.target.value)} placeholder="Nama Item" required />
            <input type="text" value={itemDetails} onChange={e => setItemDetails(e.target.value)} placeholder="Detail Item" required />
            <input type="file" accept="image/*" onChange={e => setSelectedImage(e.target.files[0])} required />
            <button type="submit">Tambah Item</button>
          </form>
          {/* Tampilkan form untuk menambahkan variasi item */}
          <form onSubmit={addItemVariant}>
            <input type="number" name="itemId" placeholder="ID Item" required />
            <input type="number" name="nominal" placeholder="Nominal" required />
            <input type="number" name="price" placeholder="Harga" required />
            <button type="submit">Tambah Variasi Item</button>
          </form>
          {items.map((item, index) => (
            <p key={index}>{item.game_name}: {item.item_name} - {item.item_details}</p>
          ))}
        </div>
      ) : (
        <div>
          <h3>User Dashboard</h3>
          {/* Tampilkan konten pengguna */}
          <p>Ini adalah konten khusus pengguna.</p>
        </div>
      )}
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Dashboard;
