import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import Home from './pages/Home';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Register from './pages/Register';
import Header from './components/Header';
import AdminRegistration from './components/AdminRegistration';
import TopUp from './pages/TopUp';
import GameTopUp from './pages/GameTopUp'; // Halaman spesifik untuk pembelian game

const App = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Check for an existing authentication token
    const accessToken = localStorage.getItem('accessToken');

    if (accessToken) {
      // Token found, set initial user state
      setUser({ accessToken });
    }
  }, []); // Empty dependency array ensures the effect runs once on mount

  const handleLogin = (userData) => {
    setUser(userData);
    // Save the access token to local storage
    localStorage.setItem('accessToken', userData.accessToken);
  };

  const handleLogout = () => {
    setUser(null);
    // Remove the access token from local storage
    localStorage.removeItem('accessToken');
  };

  const handleRegister = (userData) => {
    setUser(userData);
    // Save the access token to local storage
    localStorage.setItem('accessToken', userData.accessToken);
  };

  return (
    <Router>
      <Header />
      <Switch>
        <Route path="/home" component={Home} />
        <Route
          path="/login"
          render={(props) => (
            <Login {...props} onLogin={handleLogin} />
          )}
        />
        <Route
          path="/register"
          render={(props) => (
            <Register {...props} onRegister={handleRegister} />
          )}
        />
        <Route path="/admin/register" component={AdminRegistration} />
        <Route
          path="/dashboard"
          render={(props) =>
            user ? (
              <Dashboard {...props} onLogout={handleLogout} />
            ) : (
              <Redirect to="/login" />
            )
          }
        />
        <Route path="/topup" exact component={TopUp} />
        <Route path="/topup/valorant" component={GameTopUp} />
        {/* Tambahkan rute untuk game lain jika diperlukan */}
        <Redirect from="/" to="/home" />
      </Switch>
    </Router>
  );
};

export default App;
