// Header.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import logo from '../assets/logo.png';

import { useHistory } from "react-router-dom";

const Header = ({ setLoggedInStatus }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Periksa apakah pengguna sudah login saat komponen di-mount
    const accessToken = localStorage.getItem('accessToken');
    setIsLoggedIn(!!accessToken);
  }, []);

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem("accessToken");
    history.push("/login");
  };

  return (
    <header className="header-container">
      <header className="header-navbar">
        <Link to="/">
          <img src={logo} alt="logo" style={{ width: '60px', height: 'auto' }} />
        </Link>
        <nav>
          <ul>
            <Link to="/home" style={{ textDecoration: 'none', color: 'white' }}>
              <div>
                <span>Home</span>
              </div>
            </Link>
            {isLoggedIn && (
              <Link to="/dashboard" style={{ textDecoration: 'none', color: 'white' }}>
                <div>
                  <span>Dashboard</span>
                </div>
              </Link>
            )}
          </ul>
        </nav>
        {!isLoggedIn ? (
          <Link to="/login" className="login-button" style={{ textDecoration: 'none', color: 'white' }}>
            <div>
              <span>Login</span>
            </div>
          </Link>
        ) : (
          <button onClick={handleLogout} className="logout-button" style={{ textDecoration: 'none', color: 'white' }}>
            <div>
              <span>Logout</span>
            </div>
          </button>
        )}
        {!isLoggedIn && (
          <Link to="/register" className="register-button" style={{ textDecoration: 'none', color: 'white' }}>
            <div>
              <span>Daftar</span>
            </div>
          </Link>
        )}
      </header>
    </header>
  );
};

export default Header;
