// src/pages/Home.js
import React from 'react';
import { Link } from 'react-router-dom';

import imgValorant from '../assets/valorant.jpg';

const Home = () => {
  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', height: '100vh' }}>
      <h1>Selamat Datang di Top-Up Valorant Shop</h1>
      <p>Temukan penawaran terbaik untuk top-up Valorant di sini!</p>

      {/* Promo atau Banner */}
      <div>
        <Link to="/games/valorant">
          <img
            src={imgValorant}
            alt="Promo Banner"
            style={{
              width: '200px',
              height: '220px',
              border: '2px solid #ccc', // Ganti warna border sesuai kebutuhan
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' // Sesuaikan dengan efek shadow yang diinginkan
            }}
          />
        </Link>
      </div>
    </div>
  );
};

export default Home;
