// Header.js

import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import '../App.css';

const Header = () => {
  const [isMenuOpen, setMenuOpen] = useState(false);
  const [isLoggedIn, setLoggedIn] = useState(false); // Add state for authentication status
  const history = useHistory();

  // Function to handle logout
  const handleLogout = () => {
    // Perform logout actions (e.g., clear local storage, update state)
    localStorage.removeItem('accessToken');
    setLoggedIn(false);

    // Redirect to the login page
    history.push('/login');
  };
  

  return (
    <header className='header-container'>
      <header className='header-navbar'>
        <nav className={`nav-links ${isMenuOpen ? 'open' : ''}`}>
          <ul>
            <li>
              <Link to="/home">Home</Link>
            </li>
          </ul>
        </nav>
      
      {/* Tombol Login di luar menu */}
      <Link to="/login" className="login-button" style={{ textDecoration: 'none', color: 'white' }}>
        <div>
          <span>Login</span>
        </div>
      </Link>

      {/* Tombol Daftar di luar menu */}
      <Link to="/register" className="register-button" style={{ textDecoration: 'none', color: 'white' }}>
        <div>
          <span>Daftar</span>
        </div>
      </Link>
      </header>
    </header>
    
  );
};

export default Header;
