import React, { useState } from 'react';
import '../../App.css';

const ItemVariants = ({ variants, onSelectVariant, selectedPayment }) => {
  const [selectedNominal, setSelectedNominal] = useState(null);

  const handleVariantSelection = (variant) => {
    onSelectVariant(variant);
    setSelectedNominal(variant.nominal);
  };

  return (
    <div className='panel-topup'>
      {variants.map((variant) => (
        <div key={variant.id}>
          <label htmlFor={`variant-${variant.id}`}>
            <div
              className={`panel-topup1 ${variant.selected ? 'selected' : ''}`}
              onClick={() => handleVariantSelection(variant)}
            >
              <input
                type="radio"
                id={`variant-${variant.id}`}
                checked={variant.selected}
                onChange={() => handleVariantSelection(variant)}
              />
              <label className='label-items'>{variant.nominal} Harga {variant.price}</label>
            </div>
          </label>
        </div>
      ))}
      <div>
        <h3>Pilih Pembayaran</h3>
        <select value={selectedNominal} onChange={(e) => setSelectedNominal(e.target.value)}>
          <option value="" disabled>Pilih Nominal</option>
          {variants.map((variant) => (
            <option key={variant.id} value={variant.nominal}>
              {variant.nominal}
            </option>
          ))}
        </select>
      </div>
      <div>
        {/* Opsi pembayaran lainnya */}
        {/* ... */}
      </div>
    </div>
  );
};

export default ItemVariants;
