import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory for navigation
import { jwtDecode } from 'jwt-decode';

const Dashboard = () => {
  const [username, setUsername] = useState('');
  const history = useHistory(); // Access the history object

  useEffect(() => {
    // Ambil token dari penyimpanan lokal atau cookie
    const accessToken = localStorage.getItem('accessToken'); // Sesuaikan dengan cara penyimpanan Anda

    // Dekode token untuk mendapatkan informasi pengguna
    if (accessToken) {
      const decodedToken = jwtDecode(accessToken);
      setUsername(decodedToken.username);
    }
  }, []);

  const handleLogout = () => {
    // Clear the access token from local storage (or perform any other logout-related tasks)
    localStorage.removeItem('accessToken');

    // Redirect to the login page (or any other desired destination)
    history.push('/login');
  };

  return (
    <div>
      <h2>Welcome, {username}!</h2>
      <button onClick={handleLogout}>Logout</button>
      {/* Isi dashboard lainnya */}
    </div>
  );
};

export default Dashboard;
