export async function handleAddItem(item, items, setItems) {
    const response = await fetch('/api/items', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ item }),
    });
  
    if (response.ok) {
      setItems([...items, item]);
    } else {
      alert('Gagal menambahkan item');
    }
  }
