// Header.js


import { Link } from 'react-router-dom';
import React, { useState } from 'react';
import '../App.css';
import logo from '../assets/logo.png';

const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLoginSuccess = () => {
    // Lakukan apa pun yang diperlukan setelah login berhasil
    setIsLoggedIn(true);
  };

  // Contoh fungsi yang dipanggil setelah logout berhasil
  const handleLogout = () => {
    // Lakukan apa pun yang diperlukan setelah logout berhasil
    setIsLoggedIn(false);
  };

  return (
    <header className='header-container'>
      <header className='header-navbar'>

        <Link to="/">
          <img src={logo} alt="logo" style={{ width: '60px', height: 'auto' }} />
        </Link>
        <nav>
          <ul>
            <Link to="/home" style={{ textDecoration: 'none', color: 'white' }}>
              <div>
                <span>Home</span>
              </div>
            </Link>

            {isLoggedIn && (
              <Link to="/dashboard" style={{ textDecoration: 'none', color: 'white' }}>
                <div>
                  <span>Dashboard</span>
                </div>
              </Link>
            )}

          </ul>
        </nav>
      
        {/* Tombol Login di luar menu */}
        {!isLoggedIn ? (
          <Link to="/login" className="login-button" style={{ textDecoration: 'none', color: 'white' }}>
            <div>
              <span>Login</span>
            </div>
          </Link>
        ) : (
          // Add a logout button when logged in
          <button onClick={() => setIsLoggedIn(false)} className="logout-button" style={{ textDecoration: 'none', color: 'white' }}>
            <div>
              <span>Logout</span>
            </div>
          </button>
        )}

        {/* Tombol Daftar di luar menu */}
        {!isLoggedIn && (
          <Link to="/register" className="register-button" style={{ textDecoration: 'none', color: 'white' }}>
            <div>
              <span>Daftar</span>
            </div>
          </Link>
        )}
      </header>
    </header>
  );
};

export default Header;

