const GameDetail = () => {
  const { game } = useParams();

  const url = useMemo(() => {
    if (!game) return null;
    return `http://localhost:5001/api/getItemsByGame/${game.name}`;
  }, [game]);

  const [item, setItem] = useState(null);

  useEffect(() => {
    fetch(url)
      .then(response => response.json())
      .then(data => setItem(data[0]))
      .catch(error => console.error(error));
  }, [url]);

  useEffect(() => {
    if (item) {
      return <ItemVariants item={item} />;
    }
  }, [item]);

  return (
    <div>
      <h1>{game.name}</h1>
      <img src={game.image} alt={game.name} />
      <p>{game.description}</p>
    </div>
  );
};
