import React from 'react';
import { Link } from 'react-router-dom';

import valorant from '../assets/valorant.jpg';

const Home = () => {
  return (
    <div>
      <h1>Selamat Datang di Top-Up Game Shop</h1>
      <p>Temukan penawaran terbaik untuk top-up game favorit Anda di sini!</p>

      {/* Card Top-Up Valorant */}
      <div className="game-card">
        <img src={valorant} alt="Valorant Logo" style={{ width: '200px', height: 'auto' }} />
        <div className="card-info">
          <h2>Top-Up Valorant</h2>
          <p>Top-up saldo Valorant Anda dan dapatkan bonus eksklusif!</p>
          <p>Harga mulai dari $10.00</p>
          <Link to="/topup/valorant">
            <button>Beli Sekarang</button>
          </Link>
        </div>
      </div>

      {/* Promo atau Banner */}
      <div>
        <img src={valorant} alt="Promo Banner" style={{ width: '400px', height: 'auto' }} />
      </div>

      {/* Testimoni Pelanggan */}
      <div>
        <h2>Testimoni Pelanggan</h2>
        <p>"Top-up game di sini sangat mudah dan cepat!" - John Doe</p>
      </div>

      {/* Kontak Kami */}
      <div>
        <h2>Hubungi Kami</h2>
        <p>Jika Anda memiliki pertanyaan, silakan hubungi kami di support@example.com.</p>
      </div>
    </div>
  );
};

export default Home;
