import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';

import * as jwt_decode from 'jwt-decode';

const Dashboard = () => {
  const [items, setItems] = useState([]);
  const [username, setUsername] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const history = useHistory();

  useEffect(() => {
    const accessToken = localStorage.getItem('accessToken');

    if (accessToken) {
      const decodedToken = jwt_decode(accessToken);

      setUsername(decodedToken.username);
      setIsAdmin(decodedToken.role === 'admin'); // Periksa apakah peran pengguna adalah 'admin'
    } else {
      history.push('/login');
    }
  }, [history]);

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    history.push('/login');
  };

  const addItem = (event) => {
    event.preventDefault();
    const itemName = event.target.elements.itemName.value;
    const itemDetails = event.target.elements.itemDetails.value;
  
    fetch('http://localhost:5001/api/addItem', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        itemName,
        itemDetails,
      }),
    })
    .then(response => response.json())
    .then(data => {
      console.log(data);
      setItems([...items, { item_name: itemName, item_details: itemDetails }]);
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  };
  
  return (
    <div>
      <h2>Welcome, {username}!</h2>
      {isAdmin ? (
        <div>
          <h3>Admin Dashboard</h3>
          {/* Tampilkan form untuk menambahkan item */}
          <form onSubmit={addItem}>
            <input type="text" name="itemName" placeholder="Nama Item" required />
            <input type="text" name="itemDetails" placeholder="Detail Item" required />
            <button type="submit">Tambah Item</button>
          </form>
          {items.map((item, index) => (
            <p key={index}>{item.name}: {item.details}</p>
          ))}
        </div>
      ) : (
        <div>
          <h3>User Dashboard</h3>
          {/* Tampilkan konten pengguna */}
          <p>Ini adalah konten khusus pengguna.</p>
        </div>
      )}
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Dashboard;
