import React from 'react';
import { Link } from 'react-router-dom';

const Header = ({ isLoggedIn, setLoggedInStatus }) => {
  const handleLogout = () => {
    // Lakukan sesuatu yang perlu dilakukan setelah logout
    console.log('User successfully logged out.');

    // Hapus token akses dari local storage
    localStorage.removeItem('accessToken');
    localStorage.removeItem('userRole');

    // Update status login
    setLoggedInStatus(false);
  };

  return (
    <header className="header-container">
      <header className="header-navbar">
        <Link to="/">
          {/* Logo dan navigasi lainnya */}
        </Link>
        <nav>
          <ul>
            {isLoggedIn && (
              <Link to="/dashboard" style={{ textDecoration: 'none', color: 'white' }}>
                <div>
                  <span>Dashboard</span>
                </div>
              </Link>
            )}
          </ul>
        </nav>

        {!isLoggedIn ? (
          <>
            {/* Tombol Login di luar menu */}
            <Link to="/login" className="login-button" style={{ textDecoration: 'none', color: 'white' }}>
              <div>
                <span>Login</span>
              </div>
            </Link>

            {/* Tombol Daftar di luar menu */}
            <Link to="/register" className="register-button" style={{ textDecoration: 'none', color: 'white' }}>
              <div>
                <span>Daftar</span>
              </div>
            </Link>
          </>
        ) : (
          // Tombol Logout
          <button onClick={handleLogout}>Logout</button>
        )}
      </header>
    </header>
  );
};

export default Header;



