import React, { useState } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import imgPromotion1 from '../assets/promo.png';
import imgPromotion2 from '../assets/promo2.png';
import imgPromotion3 from '../assets/promo3.png';

const PromotionSlider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleSlideChange = (index) => {
    setCurrentSlide(index);
  };

  const sliderStyle = {
    margin: '20px 0', // Tambahkan margin atas dan bawah
    padding: '10px', // Tambahkan padding
    border: '1px solid #ddd', // Tambahkan border
    position: 'relative', // Tambahkan properti position
  };

  const arrowStyle = {
    position: 'absolute', // Tambahkan properti position
    top: '50%', // Pusatkan vertikal
    transform: 'translateY(-50%)', // Pusatkan vertikal
    backgroundColor: 'rgba(255, 255, 255, 0.5)', // Tambahkan latar belakang semi-transparan
    padding: '10px', // Tambahkan padding
    borderRadius: '5px', // Tambahkan border-radius
    cursor: 'pointer', // Ganti kursor menjadi pointer
  };

  const arrowLeftStyle = {
    ...arrowStyle,
    left: '10px',
  };

  const arrowRightStyle = {
    ...arrowStyle,
    right: '10px',
  };

  return (
    <div style={sliderStyle}>
      <Carousel showArrows={false} infiniteLoop={true} showThumbs={false} onChange={handleSlideChange}>
        <div>
          <img src={imgPromotion1} alt="Promotion 1" style={{ width: '50%', height: 'auto', marginRight: '10px' }} />
        </div>
        <div>
          <img src={imgPromotion2} alt="Promotion 2" style={{ width: '50%', height: 'auto', marginRight: '10px' }} />
        </div>
        <div>
          <img src={imgPromotion3} alt="Promotion 3" style={{ width: '50%', height: 'auto', marginRight: '10px' }} />
        </div>
      </Carousel>

      {/* Tombol Sebelumnya */}
      <div style={{ ...arrowLeftStyle, display: currentSlide === 0 ? 'none' : 'block' }}>
        <Carousel components={{ Arrow: CustomArrowPrev }} />
      </div>

      {/* Tombol Selanjutnya */}
      <div style={{ ...arrowRightStyle, display: currentSlide === 2 ? 'none' : 'block' }}>
        <Carousel components={{ Arrow: CustomArrowNext }} />
      </div>
    </div>
  );
};

// Komponen custom untuk tombol sebelumnya
const CustomArrowPrev = ({ onClick }) => (
  <div onClick={onClick}>
    <span>{'<'}</span>
  </div>
);

// Komponen custom untuk tombol selanjutnya
const CustomArrowNext = ({ onClick }) => (
  <div onClick={onClick}>
    <span>{'>'}</span>
  </div>
);

export default PromotionSlider;
