import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import Home from './pages/Home';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Register from './pages/Register';
import Header from './components/Header';
import Footer from './components/Footer';
import AdminRegistration from './components/AdminRegistration';
import GameDetail from './pages/games/GameDetail';


const App = () => {
  const [user, setUser, isLoggedIn, setLoggedInStatus] = useState(null);

  useEffect(() => {
    // Check for an existing authentication token
    const accessToken = localStorage.getItem('accessToken');

    if (accessToken) {
      // Token found, set initial user state
      setUser({ accessToken });
    }
  }, []); // Empty dependency array ensures the effect runs once on mount

  const handleLogin = (userData) => {
    setUser(userData);
    // Save the access token to local storage
    localStorage.setItem('accessToken', userData.accessToken);
  };

  const handleLogout = () => {
    setUser(null);
    // Remove the access token from local storage
    localStorage.removeItem('accessToken');
  };

  const handleRegister = (userData) => {
    setUser(userData);
    // Save the access token to local storage
    localStorage.setItem('accessToken', userData.accessToken);
  };

  return (
    <Router>
      <Header isLoggedIn={isLoggedIn} setLoggedInStatus={setLoggedInStatus} />
      <Switch>
        <Route path="/home">
          <Home />
        </Route>
        <Route
          path="/login"
          render={(props) => (
            <Login {...props} onLogin={handleLogin} />
          )}
        />
        <Route path="/games/:name" component={GameDetail} />
        <Route path="/dashboard" component={Dashboard} />
        <Route
          path="/register"
          render={(props) => (
            <Register {...props} onRegister={handleRegister} />
          )}
        />
        <Route path="/admin/register">
          {/* Komponen registrasi admin */}
          <AdminRegistration />
        </Route>
        <Route
          path="/dashboard"
          render={(props) =>
            user ? (
              <Dashboard {...props} onLogout={handleLogout} />
            ) : (
              <Redirect to="/login" />
            )
          }
        />
        {/* Rute default, redirect ke /login jika tidak ada rute yang cocok */}
        <Redirect from="/" to="/home" />
      </Switch>
      {/* Tambahkan komponen Footer di sini */}
      <Footer />
    </Router>
  );
};

export default App;
